angular.module("ui.bootstrap.demo").controller("CarouselDemoCtrl",function($scope){function assignNewIndexesToSlides(indexes){for(var i=0,l=slides.length;l>i;i++)slides[i].id=indexes.pop()}function generateIndexesArray(){for(var indexes=[],i=0;currIndex>i;++i)indexes[i]=i;return shuffle(indexes)}function shuffle(array){var tmp,current,top=array.length;if(top)for(;--top;)current=Math.floor(Math.random()*(top+1)),tmp=array[current],array[current]=array[top],array[top]=tmp;return array}$scope.myInterval=5e3,$scope.noWrapSlides=!1,$scope.active=0;var slides=$scope.slides=[],currIndex=0;$scope.addSlide=function(){var newWidth=600+slides.length+1;slides.push({image:"//unsplash.it/"+newWidth+"/300",text:["Nice image","Awesome photograph","That is so cool","I love that"][slides.length%4],id:currIndex++})},$scope.randomize=function(){var indexes=generateIndexesArray();assignNewIndexesToSlides(indexes)};for(var i=0;4>i;i++)$scope.addSlide()});