angular.module("ui.bootstrap.demo").controller("ProgressDemoCtrl",function($scope){$scope.max=200,$scope.random=function(){var type,value=Math.floor(100*Math.random()+1);type=25>value?"success":50>value?"info":75>value?"warning":"danger",$scope.showWarning="danger"===type||"warning"===type,$scope.dynamic=value,$scope.type=type},$scope.random(),$scope.randomStacked=function(){$scope.stacked=[];for(var types=["success","info","warning","danger"],i=0,n=Math.floor(4*Math.random()+1);n>i;i++){var index=Math.floor(4*Math.random());$scope.stacked.push({value:Math.floor(30*Math.random()+1),type:types[index]})}},$scope.randomStacked()});